/* 
objective: 
  define bounds

*/

(function(){

  function Element( controller ) {
    
    var instance = this
      , bounds = 0;
    
    this.__defineGetter__( 'bounds', function() {
      return bounds;
    } );
    
    this.setBounds = function( b ) {
      this.left = b.left();
      this.top = b.top();
      this.width = b.width();
      this.height = b.height();
      this.right = this.left + this.width;
      this.bottom = this.top + this.height;
      bounds = b.clone();
    };

    this.__defineSetter__( 'bounds', function( b ) {
      this.setBounds( b );
    } );
  }
  
  exports.Element = Element;

})();