/* 
objective: 
  define bounds

*/

(function(){

  function Element( controller ) {
    
    var bounds = new Rect();

    this.__defineSetter__( 'bounds', function(b){
      bounds = b.clone();
    } );

    this.__defineGetter__( 'bounds', function(){
      return bounds;
    } );

    this.layoutVertical = function( top ) {
      var height = this.bounds.height();
      this.bounds.top = top;
      return this.bounds.bottom = top + height;
    };
 
    this.layoutHorizontal = function( left ) {
      var width = this.bounds.width();
      this.bounds.left = left;
      return this.bounds.right = left + width;
    };

    this.calcAlignOffset = function() {
      if (controller.context.textAlign == 'center') {
        return this.bounds.width() * 0.5;
      }
      else if (controller.context.textAlign == 'right') {
        return this.bounds.width();
      }
      return 0;
    };

  }

  exports.Element = Element;

})();